<?php

namespace Database\Factories;

use App\Models\Category;
use Illuminate\Database\Eloquent\Factories\Factory;
use Illuminate\Support\Arr;

/**
 * @extends \Illuminate\Database\Eloquent\Factories\Factory<\App\Models\Post>
 */
class PostFactory extends Factory
{
    /**
     * Define the model's default state.
     *
     * @return array<string, mixed>
     */
    public function definition(): array
    {
        $tittle = fake()->sentence();
        
        $images = [
            "https://image.idntimes.com/post/20221110/b5c916e5cf3e6eb715edd2370d5f013e1667532273-main-5b24f5a14d3cd8dcd6ed21352b3d81b1-48a4f1a375f0aebc52457bed3e3bd6c4.jpg",
            "data:image/jpeg;base64,",
              "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ8WXZVoonfYKwxNUeuQtlUqgREjY60ZlQ9vQ&s",
              "data:image/jpeg;base64,",
     ];
             return [

            "image" => Arr::random($images),
            "tittle" => $tittle,
            "slug" => \Illuminate\Support\Str::slug($tittle),
            "category_id" => Category::inRandomOrder()->first()->id,
            "content" => fake()->paragraph(5),
            "user_id" => 1,
            "published_at" => fake()->optional()->dateTime() 
        ];
    }
}
